resources.string:
create table webapps (
   id integer primary key,
   name text not null,
   url text not null,
   fontSize integer default -1,
   userAgent text,
   certIssuedBy text,
   certIssuedTo text,
   certValidFrom text,
   certValidTo text,
   certHash text,
   cookies text
);

create table domain_names (
   id integer primary key,
   webappId integer not null,
   domain text not null
);

